import React from 'react'
import { FaReply, FaHeart } from "react-icons/fa";


export default function Tweet({tweet, author}) {
  return (
    <div className='tweet'>
      <img src={author.avatarURL} alt={`${author.name} avatar`}/>
      <div>
        <div className="user-info">
          <h5>{author.name}</h5>
          <p className="muted">{tweet.timestamp}</p>
          <p className="muted">Replying to {tweet.replyingTo}</p>
        </div>

        <p className="tweet-text">{tweet.text}</p>

        <div className="buttons">
          <FaReply />{tweet.replies.length}
          <FaHeart />{tweet.likes.length}
        </div>
      </div>
    </div>
  )
}
