import React from 'react'
import { FaReply, FaHeart } from "react-icons/fa";


export default function Tweet({name, avatarURL, date, replies, likes, text, replyingTo}) {
  return (
    <div className='tweet'>
      <img src={avatarURL} alt={`${name} avatar`}/>
      <div>
        <div className="user-info">
          <h5>{name}</h5>
          <p className="muted">{date}</p>
          {replyingTo &&
            <p className="muted">Replying to {replyingTo}</p>}
        </div>

        <p className="tweet-text">{text}</p>

        <div className="buttons">
          <FaReply />{replies}
          <FaHeart />{likes}
        </div>
      </div>
    </div>
  )
}
