import React from 'react';
import {useSelector, useDispatch} from 'react-redux';
import { FaReply, FaHeart } from "react-icons/fa";
import {handleToggleLike} from '../actions/likes';


export default function Tweet({id, name, avatarURL, date, repliesCount, likesCount, text, replyingToAuthor}) {
  const authedUser = useSelector(state => state.authedUser)
  const dispatch = useDispatch();
  const [liked, setLiked] = React.useState()

  const handleClick = () => {
    dispatch(handleToggleLike({
      id
    }))
  }
  return (
    <div className='tweet'>
      <img src={avatarURL} alt={`${name} avatar`}/>
      <div>
        <div className="user-info">
          <h5>{name}</h5>
          <p className="muted">{date}</p>
          {replyingToAuthor &&
            <p className="muted">Replying to @{replyingToAuthor}</p>}
        </div>

        <p className="tweet-text">{text}</p>

        <div className="buttons">
          <div className="button">
          <FaReply className='reply' />{repliesCount}
          </div>
          <div className="button">
          <FaHeart className='like' />{likesCount}
          </div>
        </div>
      </div>
    </div>
  )
}
