import {FETCH_TWEETS, ADD_TWEET} from '../actions/tweets';
import {TOGGLE_LIKE} from '../actions/likes';

export default function tweets(state = {}, action) {
  switch(action.type) {
    case FETCH_TWEETS:
      return {
        ...state,
        ...action.tweets
      }
    case ADD_TWEET:
      return {
        ...state,
        [action.tweet.id]: action.tweet
      }
    case TOGGLE_LIKE:
      return {
        ...state,
        [action.id]: {
          ...state[action.id]
        }
      }
    default:
      return state;
  }
}
